exports.isNumerical = function (character)
{
	return !isNaN(parseFloat(character)) && isFinite(character);
}

exports.isAlphabetical = function (character)
{
	return !(/[^a-zA-Z]/.test(character));
}

exports.isAlphanumerical = function (character)
{
	return exports.isNumerical(character) || exports.isAlphabetical(character);
}

exports.isPunctuational = function (character)
{
	return !exports.isAlphanumerical(character);
}
