var stringUtil = require('./string');

exports.extractWords = function (sentence)
{
	if (sentence.length === 0)
	{
		return [];
	}

	var potentialWords = sentence.split(' ');
	potentialWords = potentialWords.map(stringUtil.stripPunctuation);
	potentialWords = potentialWords.filter(stringUtil.isNotEmpty);

	return potentialWords;
}
